$(document).ready(function(){$(".flexslider").flexslider({animation:"slide",pauseOnHover:true,prevText:"",nextText:"",slideshowSpeed:8e3});var e="56em";var t="40em";var n=$(window).width();$(window).resize(function(){if(window.matchMedia("(min-width: "+t+")").matches){console.log("min-width: "+t+" - true");var r=$("#header_nav");if(!r.is(":visible"))r.show()}if(window.matchMedia("(max-width: "+t+")").matches){if(n==$(window).width())return false;console.log("max-width: "+t+" - true");var r=$("#header_nav");if(r.is(":visible"))r.hide()}if(window.matchMedia("(min-width: "+e+")").matches){var i=$("#mobile_high_council_chamber");if(i.is(":visible"))i.hide()}});$("#search").click(function(){var e=$(".mobile_search");if(e.is(":visible"))e.slideUp("fast");else{e.slideDown("fast");e.find("input[type='text']").focus()}});$("#menu").click(function(){var e=$("#mobile_high_council_chamber");var n=$("#header_nav");if(e.is(":visible")){e.slideUp("fast");if(window.matchMedia("(max-width: "+t+")").matches){n.slideUp("fast")}}else{e.slideDown("fast");if(window.matchMedia("(max-width: "+t+")").matches){n.slideDown("fast")}}})})